	@Test
	public void testDeepUrls() throws Exception {
		assertNotNull(plugin);
		String deepOutput = plugin.getXmlForFeatures(deepResult, callback, key);
		assertEquals(true, deepOutput.contains(DEEP_PLACENAME_URL));
		assertEquals(true, deepOutput.contains(DEEP_EPNS_URL));
		assertEquals(true, deepOutput.contains(MADSID));
		assertEquals(true, deepOutput.contains(VARIANTID));
		assertEquals(true, deepOutput.contains(ATTESTATIONS));
		assertEquals(true, deepOutput.contains(LOCATIONS));
	}
    
